import React, {useContext} from "react";
import { useTranslation } from "react-i18next";
import "./Skills.scss";
import SoftwareSkill from "../../components/softwareSkills/SoftwareSkill";
import {illustration, skillsSection} from "../../portfolio";
import {Fade} from "react-reveal";
import codingPerson from "../../assets/lottie/codingPerson";
import DisplayLottie from "../../components/displayLottie/DisplayLottie";
import StyleContext from "../../contexts/StyleContext";

export default function Skills() {
  const {isDark} = useContext(StyleContext);
  if (!skillsSection.display) {
    return null;
  }
  return (
    <div className={isDark ? "dark-mode main" : "main"} id="skills">
      <div className="skills-main-div">
        <Fade left duration={1000}>
          <div className="skills-image-div">
            {illustration.animated ? (
              <DisplayLottie animationData={codingPerson} />
            ) : (
              <img
                alt="Man Working"
                src={require("../../assets/images/developerActivity.svg")}
              ></img>
            )}
          </div>
        </Fade>
        <Fade right duration={1000}>
          <div className="skills-text-div">
            <h1
              className={isDark ? "dark-mode skills-heading" : "skills-heading"}
            >
              What I do{" "}
            </h1>
            <p
              className={
                isDark
                  ? "dark-mode subTitle skills-text-subtitle"
                  : "subTitle skills-text-subtitle"
              }
            >
              Curious Mobile Developer Always Ready to Dive into the Next Big Thing
            </p>
            <SoftwareSkill />
            <div>
              <p
                className={
                  isDark
                    ? "dark-mode subTitle skills-text"
                    : "subTitle skills-text"
                }
              >
                ⚡ Design and develop high-quality mobile applications with smooth, responsive, and user-friendly interfaces
              </p>
              <div
                className={
                  isDark
                    ? "dark-mode subTitle skills-text"
                    : "subTitle skills-text"
                }
              >
                <p style={{ marginBottom: "10px" }}>⚡ Build apps that are:</p>
                <div style={{ marginLeft: "20px", marginTop: "10px" }}>
                  <p style={{ margin: "10px 0" }}>✔ Based on strong, scalable ideas</p>
                  <p style={{ margin: "10px 0" }}>✔ Visually appealing and engaging</p>
                  <p style={{ margin: "10px 0" }}>✔ Functional and intuitive—just the way users expect</p>
                </div>
              </div>
              <p
                className={
                  isDark
                    ? "dark-mode subTitle skills-text"
                    : "subTitle skills-text"
                }
              >
                ⚡ Implement powerful features using technologies like Swift, Objective-C, SwiftUI, Flutter and Kotlin
              </p>
              <p
                className={
                  isDark
                    ? "dark-mode subTitle skills-text"
                    : "subTitle skills-text"
                }
              >
                ⚡ Integrate third-party services including Firebase (Firestore, Auth, Realtime DB, Storage), AWS, and custom REST APIs
              </p>
              <p
                className={
                  isDark
                    ? "dark-mode subTitle skills-text"
                    : "subTitle skills-text"
                }
              >
                ⚡ Follow modern architectures (MVVM, MVC, MVP, Clean Architecture) for scalable and maintainable codebases
              </p>
            </div>
          </div>
        </Fade>
      </div>
    </div>
  );
}
